import { useQuery } from "react-query";
import { fetchCoinHistory } from "../api";
import ApexChart from "react-apexcharts";

interface PriceProps {
  coinId: string;
}

interface IHistorical {
  time_open: number;
  time_close: number;
  open: string;
  high: string;
  low: string;
  close: string;
  volume: string;
  market_cap: number;
}

function Price({ coinId }: PriceProps) {
  const { isLoading, data } = useQuery<IHistorical>(["prices", coinId], () => fetchCoinHistory(coinId));

  return (
    <div>
      {isLoading ? (
        "Loading Price..."
      ) : (
        <ApexChart
          series={[
            {
              data: [
                {
                  x: new Date(1538778600000),
