import { atom, selector } from "recoil";

export interface IToDo {
  text: string;
  id: number;
  category: "Done" | "DOING" | "TO_DO";
}

export const toDoState = atom<IToDo[]>({
  key: "toDo",
  default: [],
});

export const toDoSelector = selector({
  key: "toDoSelector",
  get: ({ get }) => {
    const toDos = get(toDoState);
    return "hello";
  },
});
